<template>
  <div>
    <transition-group name="fade" tag="div">
      <div v-for="i in [currentIndex]" :key="i">
        <img :src="currentImg" />
      </div>
    </transition-group>
    <a class="prev" @click="prev" href="#">&#10094; Previous</a>
    <a class="next" @click="next" href="#">&#10095; Next</a>
  </div>
</template>
<script>

export default {
  name: "Slider",
  data() {
    return {
      images: [
        "https://www.verywellfit.com/thmb/Si0AahlbrLKYL9zU5stcvITSry4=/3000x2002/filters:no_upscale():max_bytes(150000):strip_icc()/cardiovascular-endurance-59f7fc2faad52b00100156aa.jpg",
        "https://images.unsplash.com/photo-1517836357463-d25dfeac3438?ixid=MXwxMjA3fDB8MHxzZWFyY2h8Nnx8Zml0bmVzc3xlbnwwfHwwfA%3D%3D&ixlib=rb-1.2.1&w=1000&q=80",
        // "data:image/jpeg;base64,",
        "https://www.mibluesperspectives.com/wp-content/uploads/2019/02/woman-weights-670x377.jpg",
        // "data:image/jpeg;base64,",
        "https://miro.medium.com/max/11630/0*C5Y8W-6e9OVIB3AM",
        "https://www.heart.org/-/media/images/healthy-living/fitness/flexibilityexercise.jpg",
      ],
      timer: null,
      currentIndex: 0
    };
  },

  

  methods: {
    startSlide: function() {
      this.timer = setInterval(this.next, 3000);
    },

    next: function() {
      this.currentIndex += 1;
    },
    prev: function() {
      this.currentIndex -= 1;
    }
  },

  mounted: function() {
    this.startSlide();
  },

  computed: {
    currentImg: function() {
      return this.images[Math.abs(this.currentIndex) % this.images.length];
    }
  }
};
</script>

<style scoped>
.fade-enter-active,
.fade-leave-active {
  transition: all 0.9s ease;
  overflow: hidden;
  visibility: visible;
  position: absolute;
  width:100%;
  opacity: 1;
}

.fade-enter,
.fade-leave-to {
  visibility: hidden;
  width:100%;
  opacity: 0;
}

img {
  height:600px;
  width:100%
}

.prev, .next {
  cursor: pointer;
  position: absolute;
  top: 40%;
  width: auto;
  padding: 16px;
  color: white;
  font-weight: bold;
  font-size: 18px;
  transition: 0.7s ease;
  border-radius: 0 4px 4px 0;
  text-decoration: none;
  user-select: none;
}

.next {
  right: 0;
}

.prev {
  left: 0;
}

.prev:hover, .next:hover {
  background-color: rgba(0,0,0,0.9);
}
</style>

